mapper.select:
select

from T_GAM_ASSETPOOL_INCOME_EVENT
where status != 'DELETE' AND productOid is not null

	AND productOid = #{productOid,jdbcType=VARCHAR}



	AND status = #{status,jdbcType=VARCHAR}


	AND status != #{noStatus,jdbcType=VARCHAR}


	AND assetPoolOid = #{assetPoolOid,jdbcType=VARCHAR}

ORDER BY auditTime DESC

select distinct productOid
from T_GAM_ASSETPOOL_INCOME_EVENT
where status != 'DELETE' AND productOid is not null

	AND productOid = #{productOid,jdbcType=VARCHAR}



	AND status = #{status,jdbcType=VARCHAR}


	AND status != #{noStatus,jdbcType=VARCHAR}


	AND assetPoolOid = #{assetPoolOid,jdbcType=VARCHAR}

ORDER BY auditTime DESC
