mapper.select:
    select 

    from T_MONEY_PUBLISHER_INVESTOR_INTEREST_RESULT


           productOid = #{params.productOid,jdbcType=VARCHAR}


           and allocateDate=DATE_FORMAT(#{params.incomeBaseDate}, '%Y-%m-%d')


           and status=#{params.status}


		order by createTime desc	

          limit #{offset},#{size}

select count(1)
from T_MONEY_PUBLISHER_INVESTOR_INTEREST_RESULT


       productOid = #{productOid,jdbcType=VARCHAR}


       and allocateDate=DATE_FORMAT(#{incomeBaseDate}, '%Y-%m-%d')


       and status=#{status}
